fn required_potions(monster: &char) -> u32 {
    match monster {
        'A' => 0,
        'B' => 1,
        'C' => 3,
        'D' => 5,
        _ => 0,
    }
}

fn is_monster(monster: &char) -> bool {
    "ABCD".contains(*monster)
}

fn total_required_potions(notes: &str, group_size: usize) -> u32 {
    let monsters: Vec<char> = notes.chars().collect();
    (0..monsters.len())
        .step_by(group_size)
        .into_iter()
        .map(|index| {
            let mut total = 0;
            let n_monsters: u32 = (0..group_size)
                .into_iter()
                .map(|i| {
                    let monster = monsters.get(index + i).unwrap();
                    total += required_potions(monster);
                    is_monster(monster) as u32
                })
                .sum();
            total
                + match n_monsters {
                    2 => 2,
                    3 => 6,
                    _ => 0,
                }
        })
        .sum()
}

fn part1() -> u32 {
    let notes = "";
    total_required_potions(notes, 1)
}

fn part2() -> u32 {
    let notes = "";
    total_required_potions(notes, 2)
}

fn part3() -> u32 {
    let notes = "";
    total_required_potions(notes, 3)
}

fn main() {
    println!(
        "Part 1: {}, Part 2: {}, Part 3: {}",
        part1(),
        part2(),
        part3()
    );
}
